import "core-js/modules/es.array.push.js";
import axios from 'axios';
import $ from 'jquery';
import { API_OBTENIR_REFERENCE_PAR_PAR_FAMILLE, API_RECUPERER_LISTE_OFFRE_VOYAGE, HEADERS } from '../globalConfig/globalConstConfig';
import { API_CREER_JOUR_SEMAINE_OFFRE_VOYAGE } from '../globalConfig/globalConstConfig';
import { required } from 'vuelidate/lib/validators';
export default {
  name: "ProgrammerOffreVoyage",
  data() {
    return {
      overlay: false,
      errorMsg: null,
      successMsg: null,
      jourSemaineList: [],
      jourSemaineSelectionnesList: [],
      jourSemaineModel: {
        designation: null,
        jourSemaineDesignation: null,
        offreVoyageDesignation: null
      },
      referenceJourSemaine: {
        referenceFamilleDesignation: 'referenceFamilleJourSemaine'
      },
      objectToSend: {
        datas: []
      },
      jourSemaineObject: {
        datas: []
      },
      offreVoyageObject: {
        data: {
          compagnieTransportRaisonSociale: "COMPAGNIE KOUEVI CT"
        }
      },
      offreVoyageParCompagnieTransportList: []
    };
  },
  validations: {
    programmeOffreVoyage: {
      designation: {
        required
      },
      heureArrivee: {
        required
      },
      heureDepart: {
        required
      }
    },
    jourSemaineModel: {
      designation: {
        required
      },
      jourSemaineDesignation: {
        required
      },
      offreVoyageDesignation: {
        required
      }
    }
  },
  methods: {
    //SOUMISSION DU FORMULAIRE
    submitForm() {
      this.enregistrerJourSemaineOffre();
    },
    // ENREGISTRER LE JOUR DE LA SEMAINE
    async enregistrerJourSemaineOffre() {
      if (this.jourSemaineSelectionnesList.length == 0) {
        this.errorMsg = "AUCUN JOUR N'A ÉTÉ SELECTIONNÉ";
        $(".alert-error").fadeIn();
        setTimeout(function () {
          $(".alert-error").fadeOut();
        }, 4000);
      } else {
        this.jourSemaineObject.datas = this.jourSemaineSelectionnesList;
        await axios.post(API_CREER_JOUR_SEMAINE_OFFRE_VOYAGE, this.jourSemaineObject).then(response => {
          if (response.status == 200) {
            if (response.data.status.code == 800) {
              this.successMsg = response.data.status.message;
              $(".alert-success").fadeIn();
              setTimeout(function () {
                $(".alert-success").fadeOut();
              }, 4000);
              this.jourSemaineObject.datas = [];
            } else {
              this.errorMsg = response.data.status.message;
              $(".alert-error").fadeIn();
              setTimeout(function () {
                $(".alert-error").fadeOut();
              }, 3000);
              this.jourSemaineObject.datas = [];
            }
          } else if (response.status == 204) {
            this.warningMsg = "Erreur , lors de la création de l'offre de voyage";
            $(".alert-warning").fadeIn();
            setTimeout(function () {
              $(".alert-warning").fadeOut();
            }, 3000);
            this.jourSemaineObject.datas = [];
          } else {
            this.errorMsg = "Erreur , opération de création impossible";
            $(".alert-error").fadeIn();
            setTimeout(function () {
              $(".alert-error").fadeOut();
            }, 3000);
            this.jourSemaineObject.datas = [];
          }
        }).catch(e => {
          this.errorMsg = e;
          $(".alert-error").fadeIn();
          setTimeout(function () {
            $(".alert-error").fadeOut();
          }, 4000);
          this.jourSemaineObject.datas = [];
        }).finally(() => {
          this.overlay = false;
        });
      }
    },
    //OBTENIR LISTE DES JOURS DE LA SEMAINE
    async obtenirJourSemaineList() {
      this.objectToSend.datas.push(this.referenceJourSemaine);
      await axios.post(API_OBTENIR_REFERENCE_PAR_PAR_FAMILLE, this.objectToSend, {
        headers: HEADERS
      }).then(response => {
        this.jourSemaineList = response.data.items;
      }).catch(e => {
        this.errorMsg = e;
        $(".alert-error").fadeIn();
        setTimeout(function () {
          $(".alert-error").fadeOut();
        }, 4000);
      });
    },
    //CREER UNE LISTE DE JOUR DE SEMAINE
    creerListJourSemaines(jourSemaine) {
      this.$v.$touch();
      if (this.$v.jourSemaineModel.$invalid) {
        this.errorMsg = "Vous avez omis certains champs";
        $(".alert-error").fadeIn();
        setTimeout(function () {
          $(".alert-error").fadeOut();
        }, 2000);
      } else {
        var jourSemaineSelectionne = {
          designation: null,
          jourSemaineDesignation: null,
          offreVoyageDesignation: null
        };
        jourSemaineSelectionne.designation = jourSemaine.designation;
        jourSemaineSelectionne.jourSemaineDesignation = jourSemaine.jourSemaineDesignation;
        jourSemaineSelectionne.offreVoyageDesignation = jourSemaine.offreVoyageDesignation;
        this.jourSemaineSelectionnesList.push(jourSemaineSelectionne);
      }
    },
    // RECUPERER LA LISTE DES OFFRES DE VOYAGES DISPONIBLES PAR COMPAGNIE DE TRANSPORT
    async obtenirOffreVoyageParCompagnieTransport() {
      await axios.post(API_RECUPERER_LISTE_OFFRE_VOYAGE, this.offreVoyageObject).then(response => {
        if (response.status == 200) {
          if (response.data.status.code != 800) {
            this.errorMsg = response.data.status.message;
            $(".alert-error").fadeIn();
            setTimeout(function () {
              $(".alert-error").fadeOut();
            }, 4000);
          } else {
            this.offreVoyageParCompagnieTransportList = response.data.items;
          }
        } else {
          this.errorMsg = "Erreur";
          $(".alert-error").fadeIn();
          setTimeout(function () {
            $(".alert-error").fadeOut();
          }, 4000);
        }
      }).catch(e => {
        this.errorMsg = e;
      });
    }
  },
  computed: {
    // GESTIONS DES CONTRAINTES POUR LES CHAMPS DE SAISIES
    offreVoyageDesignation() {
      const errors = [];
      if (!this.$v.jourSemaineModel.offreVoyageDesignation.$dirty) return errors;
      !this.$v.jourSemaineModel.offreVoyageDesignation.required && errors.push('Veuillez sélectionner une offre de voyage.');
      return errors;
    },
    jourSemaineReference() {
      const errors = [];
      if (!this.$v.jourSemaineModel.designation.$dirty) return errors;
      !this.$v.jourSemaineModel.designation.required && errors.push('Entrer une désignation.');
      return errors;
    },
    jourSemaineDesignation() {
      const errors = [];
      if (!this.$v.jourSemaineModel.jourSemaineDesignation.$dirty) return errors;
      !this.$v.jourSemaineModel.jourSemaineDesignation.required && errors.push('Sélectionner un jour.');
      return errors;
    }
  },
  mounted() {
    this.obtenirJourSemaineList();
    this.obtenirOffreVoyageParCompagnieTransport();
  }
};
