import "core-js/modules/es.array.push.js";
import axios from "axios";
import $ from "jquery";
import { API_OBTENIR_LISTE_DES_PAYS_DISPONIBLE, HEADERS } from '../globalConfig/globalConstConfig';
export default {
  name: 'SelectionnerPays',
  data() {
    return {
      objectValue: {},
      errorMsg: null,
      loading: true,
      options: {
        index: 0,
        size: 4
      },
      search: '',
      paysList: [],
      headers: [{
        text: 'reference',
        value: 'id'
      }, {
        text: 'Designation',
        value: 'designation'
      }, {
        text: 'Actions',
        value: 'actions',
        sortable: false
      }]
    };
  },
  methods: {
    editPays(pays) {
      const parsedPays = JSON.stringify(pays);
      localStorage.setItem('pays', parsedPays);
      this.$router.push({
        name: "modifierPays",
        params: {
          designation: pays.designation
        }
      });
    },
    // RECUPERER LA LISTE DES PAYS ENREGISTRÉS
    async readAllPaysFromApi() {
      this.loading = false;
      await axios.post(API_OBTENIR_LISTE_DES_PAYS_DISPONIBLE, this.objectValue, {
        headers: HEADERS(this.$store.state.userAuthentified.token)
      }).then(response => {
        this.paysList = response.data.items;
      }).catch(e => {
        this.errorMsg = e;
        $(".alert-error").fadeIn();
        setTimeout(function () {
          $(".alert-error").fadeOut();
        }, 4000);
      });
    },
    // SUPPRIMER UN PAYS
    supprimerPays() {}
  },
  mounted() {
    this.readAllPaysFromApi();
  }
};
