import "core-js/modules/es.array.push.js";
import { API_RECUPERER_LISTE_OFFRE_VOYAGE, HEADERS } from '../globalConfig/globalConstConfig';
import axios from 'axios';
import $ from 'jquery';
export default {
  data() {
    return {
      loading: true,
      errorMsg: false,
      warningMsg: false,
      successMsg: false,
      search: '',
      headers: [{
        text: 'reference',
        value: 'id'
      }, {
        text: 'Designation',
        value: 'designation'
      }, {
        text: 'Type de l\'offre',
        value: 'typeOffreVoyageDesignation'
      }, {
        text: 'Ville de départ',
        value: 'villeDepartDesignation'
      }, {
        text: 'Ville d\'arrivée',
        value: 'villeDestinationDesignation'
      }, {
        text: 'Actions',
        value: 'actions',
        sortable: false
      }],
      offreVoyageAvailableList: [],
      offreVoyageList: [],
      offreVoyageObject: {
        data: {
          compagnieTransportRaisonSociale: "KOUEVI CT"
        }
      }
    };
  },
  methods: {
    /*OBTENIR LA LISTE DES OFFRES DE VOYAGES DISPONIBLES POUR LES VENDEURS POUR 
        QU'ILS PUISSENT PROCÉDER À LA RESERVATION DE BILLETS POUR LES CLIENTS
    */
    async getOffreVoyageAvailables() {
      this.loading = true;
      await axios.post(API_RECUPERER_LISTE_OFFRE_VOYAGE, this.offreVoyageObject, {
        headers: HEADERS(this.$store.state.userAuthentified.token)
      }).then(response => {
        if (response.status == 200) {
          if (response.data.status.code != 800) {
            this.errorMsg = response.data.status.message;
            $(".alert-error").fadeIn();
            setTimeout(function () {
              $(".alert-error").fadeOut();
            }, 4000);
          } else {
            this.offreVoyageList = response.data.items;
            this.offreVoyageList.forEach(element => {
              if (element.isActif == true) {
                this.offreVoyageAvailableList.push(element);
              }
            });
          }
        } else {
          this.errorMsg = "Erreur";
          $(".alert-error").fadeIn();
          setTimeout(function () {
            $(".alert-error").fadeOut();
          }, 4000);
        }
      }).catch(e => {
        this.errorMsg = e;
      }).finally(() => {
        this.loading = false;
      });
    },
    storeOffreVoyageItem(item) {
      const parsedOffreSelected = JSON.stringify(item);
      localStorage.setItem('offreVoyageSelected', parsedOffreSelected);
    },
    reserverTicket(offreVoyage) {
      this.storeOffreVoyageItem(offreVoyage);
      this.$router.push({
        path: "/reservationBillet"
      });
    }
  },
  mounted() {
    this.getOffreVoyageAvailables();
  }
};
