import axios from 'axios';
import $ from 'jquery';
import { API_RECUPERER_LISTE_GARES_PAR_COMPAGNIE, HEADERS } from '../globalConfig/globalConstConfig';
export default {
  name: 'SelectionnerGareCompagnieTransport',
  data() {
    return {
      errorMsg: null,
      loading: true,
      search: '',
      headers: [{
        text: 'reference',
        value: 'id'
      }, {
        text: 'Designation',
        value: 'designation'
      }, {
        text: 'E-mail',
        value: 'email'
      }, {
        text: 'Addresse',
        value: 'adresseLocalisation'
      }, {
        text: 'Telephone',
        value: 'telephone1'
      }, {
        text: 'Actions',
        value: 'actions',
        sortable: false
      }],
      gareListObject: {
        data: {
          compagnieTransportRaisonSociale: "COMPAGNIE KOUEVI CT"
        }
      },
      gareDisponiblesList: []
    };
  },
  methods: {
    // RECUPERER LA LISTE DES GARES ENREGISTRÉS PAR COMPAGNIE DE TRANSPORT
    async obtenirGareListParCompagnieTransport() {
      this.loading = true;
      axios.post(API_RECUPERER_LISTE_GARES_PAR_COMPAGNIE, this.gareListObject, {
        headers: HEADERS(this.$store.state.userAuthentified.token)
      }).then(response => {
        if (response.status == 200) {
          if (response.data.status.code != 800) {
            this.errorMsg = response.data.status.message;
            $(".alert-error").fadeIn();
            setTimeout(function () {
              $(".alert-error").fadeOut();
            }, 4000);
          } else {
            this.gareDisponiblesList = response.data.items;
          }
        } else {
          this.errorMsg = "Erreur";
          $(".alert-error").fadeIn();
          setTimeout(function () {
            $(".alert-error").fadeOut();
          }, 4000);
        }
      }).catch(e => {
        this.errorMsg = e;
      }).finally(() => {
        this.loading = false;
      });
    }
  },
  mounted() {
    this.obtenirGareListParCompagnieTransport();
  }
};
