import "core-js/modules/es.array.push.js";
import axios from "axios";
import $ from "jquery";
import { API_OBTENIR_LISTE_DES_VILLES_DISPONIBLE, HEADERS } from '../globalConfig/globalConstConfig';
export default {
  name: 'SelectionnerVille',
  data() {
    return {
      errorMsg: null,
      objectValue: {},
      loading: true,
      options: {
        index: 0,
        size: 4
      },
      search: '',
      villesList: [],
      headers: [{
        text: 'reference',
        value: 'id'
      }, {
        text: 'Designation',
        value: 'designation'
      }, {
        text: 'Pays',
        value: 'paysDesignation'
      }, {
        text: 'Actions',
        value: 'actions',
        sortable: false
      }]
    };
  },
  methods: {
    editVille(ville) {
      const parsedVille = JSON.stringify(ville);
      localStorage.setItem('ville', parsedVille);
      this.$router.push({
        path: "/modifierVille"
      });
    },
    // RECUPERER LA LISTE DES VILLES ENREGISTRÉES
    async readAllVilleFromApi() {
      this.loading = false;
      await axios.post(API_OBTENIR_LISTE_DES_VILLES_DISPONIBLE, this.objectValue, {
        headers: HEADERS(this.$store.state.userAuthentified.token)
      }).then(response => {
        this.villesList = response.data.items;
      }).catch(e => {
        this.errorMsg = e;
        $(".alert-error").fadeIn();
        setTimeout(function () {
          $(".alert-error").fadeOut();
        }, 4000);
      });
    },
    // SUPPRIMER UNE VILLE
    async supprimerVille() {}
  },
  mounted() {
    this.readAllVilleFromApi();
  }
};
